#!/bin/bash

sudo apt update && sudo apt upgrade -y; sudo apt install -y python3 python3-pip virtualbox unzip git python3.10-venv ansible; wget https://releases.hashicorp.com/vagrant/2.4.3/vagrant_2.4.3-1_amd64.deb; sudo dpkg -i vagrant_2.4.3-1_amd64.deb; sudo apt-get install -f; rm vagrant_2.4.3-1_amd64.deb; sudo pip3 install --force-reinstall pyOpenSSL cryptography boto3; wget https://releases.hashicorp.com/terraform/1.10.4/terraform_1.10.4_linux_amd64.zip; unzip terraform_1.10.4_linux_amd64.zip; sudo mv terraform /usr/local/bin/; rm terraform_1.10.4_linux_amd64.zip; terraform -v; git clone https://github.com/splunk/attack_range.git; cd attack_range; python3 -m venv .venv; source .venv/bin/activate; pip install --upgrade pip; pip install -r requirements.txt; sudo apt install -y awscli; aws configure; python attack_range.py configure
